        [TestMethod]
        public void TestMethod1()
        {
            var testService = new MazeService();
            var mazeString = "##########\r\n#A...#...#\r\n#.#.##.#.#\r\n#.#.##.#.#\r\n#.#....#B#\r\n#.#.##.#.#\r\n#....#...#\r\n##########";
