            var answer = testService.solveMaze(mazeString);
            var answerEncoded = Encoding.UTF8.GetBytes(answer.ToString());
            var uncoded = Encoding.UTF8.GetString(answerEncoded, 0, answerEncoded.Length);
            Assert.AreEqual(14, answer.steps);
            Assert.AreEqual(completedMaze, answer.solution);
        }

